import React, { useState } from 'react';
import axios from 'axios';
function MultipleFileUpload() {
const [selectedFiles, setSelectedFiles] = useState([]);
const token =sessionStorage.getItem("authToken")
const [buttonText, setButtonText] = useState('Analysis Start');



// 파일 선택 시 호출되는 함수
const handleFileChange = (e) => {
    setSelectedFiles(e.target.files); // 선택된 파일들을 상태에 저장
};

// 파일 업로드 처리 함수
const handleUpload = async () => {
    if (selectedFiles.length === 0) {
        alert('No files selected.');
        return;
    }

    const formData = new FormData();

    // 선택된 파일들을 FormData에 추가
    for (const file of selectedFiles) {
        formData.append('files', file);
    }

    try {
        const response = await fetch('http://192.168.0.142:8080/images/imagesFolder', {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${token}`,
            },
            body: formData,
        });

        if (response.ok) {
            alert('Files uploaded and analyzed successfully!');
        } else {
            alert('Failed to upload and analyze files.');
        }
    } catch (error) {
        console.error('Error:', error);
        alert('An error occurred during the file upload.');
    }
};

const [isProcessing, setIsProcessing] = useState(false);

    // 이미지 처리 시작 함수
    const handleAnalysis = async () => {
        if (!isProcessing) {
            setButtonText('Analyzing...');
            setIsProcessing(true);
            setResult(null);

            try {
                const response = await fetch('http://192.168.0.142:8080/images/processImages', {
                    method: 'GET',
                    headers: {
                      'Authorization': `Bearer ${token}`,
                  },
                });

                if (response.ok) {
                    const data = await response.json();
                    setResult(data);
                    setButtonText('Next');
                } else {
                    alert('Failed to process images.');
                }
            } catch (error) {
                console.error('Error:', error);
                alert('An error occurred during image processing.');
            } finally {
                setIsProcessing(false);
            }
        } else {
            // Next 버튼 클릭 시 분석 반복 (비슷한 방식으로 API 호출)
            // 여기에 반복 분석 로직을 추가할 수 있습니다.
            setButtonText('Analyzing...');
            setIsProcessing(true);


            try {
                const response = await fetch('http://192.168.0.142:8080/images/processImages', {
                    method: 'GET',
                    headers: {
                      'Authorization': `Bearer ${token}`,
                  },
                });

                if (response.ok) {
                    const data = await response.json();
                    setResult(data);
                    setButtonText('Next');
                } else {
                    alert('Failed to process images.');
                }
            } catch (error) {
                console.error('Error:', error);
                alert('An error occurred during image processing.');
            } finally {
                setIsProcessing(false);
            }
        }
    };

return (
    <div>
        <h1>Upload Multiple Images and View Analysis Result</h1>
        <input
            type="file"
            onChange={handleFileChange}
            multiple // 여러 파일 선택 가능
        />
        <button onClick={handleUpload}>Upload</button>


        <button onClick={handleAnalysis} disabled={isProcessing}>
                {buttonText}
            </button>
            {result && (
                <div>
                    <h2>Analysis Result:</h2>
                    <p><strong>Original Filename:</strong> {result.originalFilename}</p>
                    <p><strong>Original File Path:</strong> {result.originalFilePath}</p>
                    <p><strong>Flask Transmission Complete:</strong> {result.flaskTransmissionComplete ? 'Yes' : 'No'}</p>
                    <p><strong>License Plate File Path:</strong> {result.licensePlateFilePath}</p>
                    <p><strong>License Plate Recognition Status:</strong> {result.licensePlateRecognitionStatus ? 'Recognized' : 'Not Recognized'}</p>
                    <p><strong>Recognized License Plate:</strong> {result.recognizedLicensePlate}</p>
                </div>
            )}
    </div>


);
}
export default MultipleFileUpload;
